using System;
using System.Collections.Generic;
using System.Linq;
using System.Text;
using System.Threading.Tasks;

namespace leetcode_exercise
{
    internal class FindSubstring_
    {
        public IList<int> FindSubstring(string s, string[] words)
        {

            return Permute(s,words).ToList();
        }

      
        public  HashSet<int> Permute(string s,string[] words)
        {
            HashSet<int> list = new();
            HashSet<string> strs= new HashSet<string>();
            foreach (string word in words)
            {
                strs.Add(word);
            }
            int offset = 1;
            if (strs.Count == words.Length)
                offset = words[0].Length;
            PermuteRecur(s,new bool[words.Length], words, 0, new List<string>(), list,offset);
            return list;
        }

         void PermuteRecur(string s,bool[] vis, string[] words, int idx, List<string> l, HashSet<int> ans, int offset)
        {
            if (idx == words.Length)
            {
                string str= string.Join("", l);
                int index=0 ;
                int off = 0;
                while((index=s.IndexOf(str,off))>=0)
                {
                    off =index+offset;
                    ans.Add(index);
                }
                //l.Clear();
                return;
            }
            for (int i = 0; i < words.Length; ++i)
            {
                if (vis[i])
                {
                    continue;
                }
                l.Add(words[i]);
                vis[i] = true;
                PermuteRecur(s,vis, words, idx + 1, l, ans,offset);
                l.RemoveAt(idx);
                vis[i] = false;
            }
        }
        static void Main(string[] args)
        {
            FindSubstring_ f = new();
            {
                var res = f.FindSubstring("barfoothefoobarman", new string[] { "foo", "bar" });
                Console.WriteLine(string.Join(",", res));
            }
            {
                var res = f.FindSubstring("wordgoodgoodgoodbestword", new string[] { "word", "good", "best", "good" });
                Console.WriteLine(string.Join(",", res));
            }
            {
                var res = f.FindSubstring("aaaaaaaaaaaaaa", new string[] { "aa", "aa" });
                Console.WriteLine(string.Join(",", res));
            }
            {
                var str = "";
                var res = f.FindSubstring("", new string[] { "dhvf", "sind", "ffsl", "yekr", "zwzq", "kpeo", "cila", "tfty", "modg", "ztjg", "ybty", "heqg", "cpwo", "gdcj", "lnle", "sefg", "vimw", "bxcb" });
                Console.WriteLine(string.Join(",", res));
                Console.WriteLine(str.IndexOf("dhvfsindffslyekrzwzqkpeocilatftyybtylnleztjgmodggdcjvimwsefgheqgbxcbcpwo"));
            }
        }
    }
