    internal class FindSubstring_
    {
        public IList<int> FindSubstring(string s, string[] words)
        {
            List<int> result = new List<int>();
            Dictionary<string, int> dict = new();
            foreach (var item in words)
            {
                if(dict.ContainsKey(item))
                    dict[item]++;
                else dict[item] = 0;
            }
            int wordLen = words[0].Length;
            for(int i=0;i<wordLen;++i)
            {
                string substr = s.Substring(i, wordLen);
          
                var newDict = new Dictionary<string, int>(dict);
                         
                for(int j=i;j<=s.Length-wordLen*words.Length;j+=wordLen)
                {   
                    
                    var  newSubstr=s.Substring(i+wordLen*j, wordLen);
                    if(!newDict.ContainsKey(newSubstr))
                    {
                        newDict = new Dictionary<string, int>(dict);

                    }else
                    {
                        if (newDict[newSubstr] > 0)
                        {
                            newDict[newSubstr]--;
                        }
                        else
                        {
                            newDict.Remove(newSubstr);
                        }
                    }

                    
                    if (newDict.Count == 0)
                        result.Add(i);

                }

            }
            return result;
        }

      
    

      
